import { React, Fragment } from "react";
import SearchBar from "../components/SearchBar";
import Table from "../components/Table";
import { Link } from "react-router-dom";
import { Menu, Transition } from '@headlessui/react';

function TurfSheet() {

    function classNames(...classes) {
        return classes.filter(Boolean).join(' ');
    }

    const [orderByField, setOrderByField] = useState("lastName");
    const [orderByOrdering, setOrderByOrdering] = useState(true);

    const handleOrderByOnClick = async (e) => {
        let field = e.target.name;
        if (orderByField === field) {
            setOrderByOrdering(!orderByOrdering);
        } else {
            setOrderByField(field);
            setOrderByOrdering(true);
        }
    }

    const chevDown = (
        <svg className="w-6 h-6 absolute right-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M19 9l-7 7-7-7" /></svg>
    );

    const chevUp = (
        <svg className="w-6 h-6 absolute right-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M5 15l7-7 7 7" /></svg>
    );

    return (
        <>
            <div className="inline-flex items-center">
                <SearchBar />



                <Menu as="div" className="relative inline-block text-left">
                    <Menu.Button className="inline-flex items-center text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 font-medium rounded-lg text-sm px-5 py-2.5 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600">
                        Order By
                        <svg className="ml-1 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" /></svg>
                    </Menu.Button>

                    <Transition
                        as={Fragment}
                        enter="transition ease-out duration-100"
                        enterFrom="transform opacity-0 scale-95"
                        enterTo="transform opacity-100 scale-100"
                        leave="transition ease-in duration-75"
                        leaveFrom="transform opacity-100 scale-100"
                        leaveTo="transform opacity-0 scale-95"
                    >
                        <Menu.Items className="origin-top-right absolute w-60 rounded-md shadow-lg bg-white focus:outline-none">
                            <div className="py-1">
                                <Menu.Item>
                                    {({ active }) => (
                                        <button
                                            role="button"
                                            className={classNames(
                                                active ? 'bg-gray-100 text-gray-900' : 'text-gray-700',
                                                'block px-4 py-2 text-sm font-normal w-full inline-flex items-center'
                                            )}
                                            onClick={handleOrderByOnClick}
                                            name="lastName"
                                        >
                                            Last Name
                                            {orderByField === "lastName" ? (<></>) : <></>}
                                            {chevDown}
                                        </button>
                                    )}
                                </Menu.Item>
                            </div>
                        </Menu.Items>
                    </Transition>
                </Menu>



                <Link to="/contacts/add" className="absolute right-6">
                    <button type="button" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Add Contact</button>
                </Link>
            </div>
            <Table />
        </>
    );
}

export default TurfSheet;
