import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import Tag from "../components/Tag";

function CreateScript() {
    const navigate = useNavigate();

    const [script, setScript] = useState({});
    const [tags, setTags] = useState([]);

    // Update script state on input change
    const inputChange = (e) => {
        const { name, value } = e.target;
        setScript({
            ...script,
            [name]: value,
        });
    };

    // Handle enter key on tags
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            console.log('do validate');
            const { name, value } = e.target;
            console.log(name);
            console.log(value);
        }
    };

    // Delete tag
    const deleteTag = async (value) => {
        setTags(tags.filter(tag => tag.value !== value));
    };

    // Create script
    const onSubmit = async (e) => {
        e.preventDefault();
        console.log("hi");
        try {
            const response = await fetch(
                "http://localhost:8000/scripts/create/",
                {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(script)
                }
            );
            navigate("/");
        } catch (error) {
            console.error(error.message);
        }
    };

    return (
        <form>
            <div className="m-6 mb-0 bg-white rounded-lg border shadow-md dark:border-gray-700 dark:bg-gray-800">
                <h5 className="m-3 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Create Script</h5>
            </div>
            <div className="p-6">
                <div className="mb-4">
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Title</label>
                    <input type="text" name="title" value={script.title || ""} onChange={inputChange} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Description</label>
                    <input type="text" name="description" value={script.description || ""} onChange={inputChange} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Timeline</label>
                    <input datepicker="" type="text" name="timeline" value={script.timeline || ""} onChange={inputChange} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Tags</label>
                    <input type="text" name="tags" value={script.tags || ""} onChange={inputChange} onKeyDown={handleKeyDown} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                    <div>{tags.map((tag) => (
                        <Tag value={tag.value} onDelete={deleteTag} />
                    ))}</div>
                </div>
                <div>
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-400">Script</label>
                    <textarea type="text" name="body" value={script.body || ""} onChange={inputChange} rows={4} className="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Write your script here..." required />
                </div>
            </div>

            <div className="pl-6">
                <button type="button" onClick={onSubmit} className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-3 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Create</button>
                <button type="button" className="text-gray-900 bg-white border border-gray-300 hover:bg-gray-100 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-gray-600 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-700 dark:focus:ring-gray-800">Save Draft</button>
                <Link to="/scripts">
                    <button type="reset" className="absolute right-6 text-white bg-red-700 hover:bg-red-800 focus:ring-4 focus:ring-red-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-red-600 dark:hover:bg-red-700 dark:focus:ring-red-900">Discard Draft</button>
                </Link>
            </div>
        </form>
    );
}

export default CreateScript;
