import { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import './Script.css';

function Script() {
    const navigate = useNavigate();
    const initialScript = useLocation().state;

    const [script, setScript] = useState(initialScript);
    const [contacts, setContacts] = useState([]);
    const [contactIndex, setContactIndex] = useState(0);
    const [replacedBody, setReplacedBody] = useState("");

    // Get all contacts
    const getContacts = async () => {
        try {
            const response = await fetch("http://localhost:8000/contacts");
            const jsonData = await response.json();

            setContacts(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    const updateBody = () => {
        var updatedBody = script.body;
        if (contacts[contactIndex] == null) return;
        const { first_name, last_name, house, suite, class_year, city, state,
            contact_status, pte_status, registration_status, stage_of_voting_process } =
            contacts[contactIndex];
        const arr = [first_name, last_name, house, suite, class_year, city, state,
            contact_status, pte_status, registration_status, stage_of_voting_process];
        const fieldValues = ["{first_name}", "{last_name}", "{house}", "{suite}", "{class_year}",
            "{city}", "{state}", "{contact_status}", "{pte_status}", "{registration_status}", "{stage_of_voting_process}"];
        for (let i = 0; i < fieldValues.length; i++) {
            updatedBody = updatedBody.replaceAll(fieldValues[i], arr[i]);
        }
        setReplacedBody(updatedBody);
    };

    const prev = () => {
        if (contactIndex - 1 < 0) return;
        setContactIndex(contactIndex - 1);
    }

    const next = () => {
        if (contactIndex + 1 >= contacts.length) return;
        setContactIndex(contactIndex + 1);
    };

    useEffect(() => {
        getContacts();
    }, []);

    useEffect(() => {
        updateBody();
    }, [contactIndex]);

    useEffect(() => {
        updateBody();
    }, [contacts]);

    return (
        <>
            <p className="script m-4 text-lg">{replacedBody}</p>


            <button type="button" class="text-blue-700 inline-flex items-center hover:text-white border border-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-0 py-2.5 text-center mr-2 mb-2 dark:border-blue-500 dark:text-blue-500 dark:hover:text-white dark:hover:bg-blue-600 dark:focus:ring-blue-800">
                <svg class="w-6 h-6" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clip-rule="evenodd"></path></svg>Previous
            </button>


            <button type="button" onClick={prev} className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-3 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Previous</button>
            <button type="button" onClick={next} className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-3 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Next</button>
        </>
    );
}

export default Script;
