import React from "react";
import { useState, useEffect } from "react";
import Contact from "./Contact";
import SearchBar from "./SearchBar";
import TableHeader from "./TableHeader";

function Table() {
  const [contacts, setContacts] = useState([]);

  // Get all contacts
  const getContacts = async () => {
    try {
      const response = await fetch("http://localhost:8000/contacts");
      const jsonData = await response.json();

      setContacts(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  // Delete contact
  const deleteContact = async (id) => {
    if (window.confirm("Are you sure you want to delete this contact?")) {
      try {
        const response = await fetch(`http://localhost:8000/contacts/${id}`, {
          method: "DELETE"
        });

        setContacts(contacts.filter(contact => contact.id !== id));
      } catch (error) {
        console.error(error.message);
      }
    }
  };

  useEffect(() => {
    getContacts();
  }, []);

  return (
    <>
      <SearchBar onContactsChange={setContacts} />
      {contacts.length > 0 ? (
        // <table className="table table-striped table-sm my-3">
        <table className="w-full">
          <thead>
            <tr>
              <th className="bg-blueGray-50 text-blueGray-500 align-middle border border-solid border-blueGray-100 py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left">
                <span className="ml-1">Last Name</span>
              </th>
              <th className="px-6 bg-blueGray-50 text-blueGray-500 align-middle border border-solid border-blueGray-100 py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left">First Name</th>
              <TableHeader content="Email" />
              <TableHeader content="Phone Number" />
              <TableHeader content="House/Yard" />
              <TableHeader content="Suite" />
              <TableHeader content="Mailbox Center" />
              <TableHeader content="Mailbox Number" />
              <TableHeader content="Class Year" />
              <TableHeader content="Home City" />
              <TableHeader content="Home State" />
              <TableHeader content="Should Contact" />
              <TableHeader content="Contact Status" />
              <TableHeader content="PTE Status" />
              <TableHeader content="Registration Status" />
              <TableHeader content="Stage of Voting Process" />
              <TableHeader content="Actions" />
              {/* <th>Phone Number</th>
              <th>House/Yard</th>
              <th>Suite</th>
              <th>Mailbox Center</th>
              <th>Mailbox Number</th>
              <th>Class Year</th>
              <th>Home City</th>
              <th>Home State</th>
              <th>Should Contact</th>
              <th>Contact Status</th>
              <th>PTE Status</th>
              <th>Registration Status</th>
              <th>Stage of Voting Process</th>
              <th>
                <span className="mr-1">Actions</span>
              </th> */}
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <Contact key={contact.id} contact={contact} onDelete={deleteContact} />
            ))}
          </tbody>
        </table>
      ) : (
        <h4 className="m-3">No Contacts</h4>
      )}
      <div className="max-w-sm bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700">
        <div className="flex justify-end px-4 pt-4">
          <button id="dropdownButton" data-dropdown-toggle="dropdown" className="hidden sm:inline-block text-gray-500 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 rounded-lg text-sm p-1.5" type="button">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path d="M10 6a2 2 0 110-4 2 2 0 010 4zM10 12a2 2 0 110-4 2 2 0 010 4zM10 18a2 2 0 110-4 2 2 0 010 4z" /></svg>
          </button>
          {/* Dropdown menu */}
          <div id="dropdown" className="hidden z-10 w-44 text-base list-none bg-white rounded divide-y divide-gray-100 shadow dark:bg-gray-700">
            <ul className="py-1" aria-labelledby="dropdownButton">
              <li>
                <a href="#" className="block py-2 px-4 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Edit</a>
              </li>
              <li>
                <a href="#" className="block py-2 px-4 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Export Data</a>
              </li>
              <li>
                <a href="#" className="block py-2 px-4 text-sm text-red-600 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Delete</a>
              </li>
            </ul>
          </div>
        </div>
        <div className="flex flex-col items-center pb-10">
          <img className="mb-3 w-24 h-24 rounded-full shadow-lg" src="/docs/images/people/profile-picture-3.jpg" alt="Bonnie image" />
          <h5 className="mb-1 text-xl font-medium text-gray-900 dark:text-white">Bonnie Green</h5>
          <span className="text-sm text-gray-500 dark:text-gray-400">Visual Designer</span>
          <div className="flex mt-4 space-x-3 lg:mt-6">
            <a href="#" className="inline-flex items-center py-2 px-4 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Add friend</a>
            <a href="#" className="inline-flex items-center py-2 px-4 text-sm font-medium text-center text-gray-900 bg-white rounded-lg border border-gray-300 hover:bg-gray-100 focus:ring-4 focus:ring-blue-300 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-700 dark:focus:ring-gray-800">Message</a>
          </div>
        </div>
      </div>
    </>
  );
}

export default Table;
