import { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import './Script.css';

function Script() {
    const navigate = useNavigate();
    const initialScript = useLocation().state;

    const [script, setScript] = useState(initialScript);
    const [contacts, setContacts] = useState([]);
    const [contact, setContact] = useState({});

    // Get all contacts
    const getContacts = async () => {
        try {
            const response = await fetch("http://localhost:8000/contacts");
            const jsonData = await response.json();

            setContacts(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    const next = () => {
        console.log(contacts[0]);
        console.log(contact);
    };

    useEffect(() => {
        getContacts();
    }, []);

    // useEffect(() => {
    //     setContact(contacts[0]);
    // }), [contacts];

    return (
        <>
            <p className="script">{script.body}</p>
            <button type="button" onClick={next} className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-3 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Next</button>
        </>
    );
}

export default Script;
