import { useState } from "react";
import { useNavigate } from "react-router-dom";

function CreateScript() {
    const navigate = useNavigate();

    const [script, setScript] = useState({});

    // Update script state on input change
    const inputChange = (e) => {
        const { name, value } = e.target;
        setScript({
            ...script,
            [name]: value,
        });
    };

    // Create script
    const onSubmit = async (e) => {
        // e.preventDefault();
        // try {
        //     const response = await fetch(
        //         "http://localhost:8000/add-contact/",
        //         {
        //             method: "POST",
        //             headers: { "Content-Type": "application/json" },
        //             body: JSON.stringify(contact)
        //         }
        //     );
        //     navigate("/");
        // } catch (error) {
        //     console.error(error.message);
        // }
        console.log("create script test");
    };

    return (
        // <form onSubmit={onSubmit}>
        //     <div className="form-group m-3">
        //         <label>Script</label>
        //         <input
        //             type="text"
        //             placeholder="Script"
        //             name="body"
        //             value={script.body || ""}
        //             onChange={inputChange}
        //             className="form-control"
        //         ></input>
        //     </div>

        //     <input type="submit" value="Create" className="ml-3 btn btn-outline-primary" />
        // </form>
        <form>
            <div href="#" className="flex flex-col items-center bg-white rounded-lg border shadow-md md:flex-row md:max-w-xl dark:border-gray-700 dark:bg-gray-800">
                <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Create Script</h5>
            </div>
            <div className="p-6">
                <div className="mb-4">
                    <label htmlFor="title" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Title</label>
                    <input type="text" id="title" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label htmlFor="description" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Description</label>
                    <input type="text" id="description" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label htmlFor="timeline" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Timeline</label>
                    <input type="text" id="timeline" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div className="mb-4">
                    <label htmlFor="tags" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Tags</label>
                    <input type="text" id="tags" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required />
                </div>
                <div>
                    <label htmlFor="script" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-400">Script</label>
                    <textarea id="script" rows={4} className="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Write your script here..." defaultValue={""} />
                </div>
            </div>

            <div className="pl-6">
                <button type="submit" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-3 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Create</button>
                <button type="button" className="text-gray-900 bg-white border border-gray-300 hover:bg-gray-100 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-gray-600 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-700 dark:focus:ring-gray-800">Save Draft</button>
                <button type="button" className="absolute right-6 text-white bg-red-700 hover:bg-red-800 focus:ring-4 focus:ring-red-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-red-600 dark:hover:bg-red-700 dark:focus:ring-red-900">Discard Draft</button>
            </div>
        </form>
    );
}

export default CreateScript;
